mapper.select:
SELECT * 
FROM reviews

    status = 'ACTIVE' 

        AND ${key} LIKE CONCAT('%', #{word}, '%')



    ORDER BY ${orderBy} ${orderDir}

LIMIT #{offset}, #{size}

mapper.update:
UPDATE reviews
SET status = 'INACTIVE'
WHERE review_id = #{reviewId};

UPDATE reviews
SET 
    review_title = #{reviewTitle}, 
    review_content = #{reviewContent}
WHERE review_id = #{reviewId} AND status = 'ACTIVE';
